<?phpob_start();session_start();//test if the admin is already logged in .. redirect to tghe links page //else //one of two things .. Show the form .. or process its dataif($_SESSION['logged_in_admin'] == 'yes')	{//Admin logged in	//header("location:index2.php");	include_once("links.php");	}else	{//Not logged in .. show the form or process it .	if(count($_POST)>0 and $_POST['admin_username'] != '')		{//------------------------------------------------------------------- process data				if($_POST['admin_username'] == 'admin' and $_POST['admin_password'] == '123456')			{//true login data			$_SESSION['logged_in_admin'] = 'yes' ;			header("location:".$_SERVER['PHP_SELF']);			}		else			{//Invalid login data			print "Go away Ya IBN .... ";			}						}	else		{//------------------------------------------------------------------- show login form		print "						<table style='width:150px;border:1px dotted #cccccc;'>					<form action='$_SERVER[PHP_SELF]' method='post'>						<tr>							<td>Username </td> 							<td><input type='text' name='admin_username' /> </td>						</tr>						<tr>								<td>Password </td> 							<td><input type='password' name='admin_password' /> </td> 						</tr>						<tr>							<td colspan='2'> <input type='submit' value='login' /> </td> 						</tr>					</form>					</table>								";		}	}?>